function varargout = GLW_bootstrap(varargin)
% GLW_BOOTSTRAP MATLAB code for GLW_bootstrap.fig




% Begin initialization code - DO NOT EDIT
gui_Singleton = 1;
gui_State = struct('gui_Name',       mfilename, ...
                   'gui_Singleton',  gui_Singleton, ...
                   'gui_OpeningFcn', @GLW_bootstrap_OpeningFcn, ...
                   'gui_OutputFcn',  @GLW_bootstrap_OutputFcn, ...
                   'gui_LayoutFcn',  [] , ...
                   'gui_Callback',   []);
if nargin && ischar(varargin{1})
    gui_State.gui_Callback = str2func(varargin{1});
end

if nargout
    [varargout{1:nargout}] = gui_mainfcn(gui_State, varargin{:});
else
    gui_mainfcn(gui_State, varargin{:});
end
% End initialization code - DO NOT EDIT





% --- Executes just before GLW_bootstrap is made visible.
function GLW_bootstrap_OpeningFcn(hObject, eventdata, handles, varargin)
% This function has no output args, see OutputFcn.
% hObject    handle to figure
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
% varargin   command line arguments to GLW_bootstrap (see VARARGIN)
% Choose default command line output for GLW_bootstrap
handles.output = hObject;
% Update handles structure
guidata(hObject, handles);
%output
set(handles.processbutton,'Userdata',varargin{3});
axis off;
%fill listbox with inputfiles
set(handles.filebox,'String',varargin{2});
%load header
inputfiles=get(handles.filebox,'String');
header=LW_load_header(inputfiles{1});
%fill chanbox with channel labels
chanstring={};
for chanpos=1:length(header.chanlocs);
    chanstring{chanpos}=header.chanlocs(chanpos).labels;
end;
set(handles.chanbox,'String',chanstring);
%set default reference interval
set(handles.xedit2,'String',num2str(0));
set(handles.xedit1,'String',num2str(header.xstart));


    




