% --- Executes on button press in processbutton.
function processbutton_Callback(hObject, eventdata, handles)
% hObject    handle to processbutton (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
inputfiles=get(handles.filebox,'String');
%channels
channels=get(handles.chanbox,'Value');
%baseline_interval
baseline_interval(1)=str2num(get(handles.xedit1,'String'));
baseline_interval(2)=str2num(get(handles.xedit2,'String'));
%num_bootstrap
num_bootstrap=str2num(get(handles.numbootstrapedit,'String'));
fig_title=get(handles.figure,'name');
set(handles.figure,'name',[fig_title ' (Running)']);
update_status=get(handles.processbutton,'UserData');
update_status.function(update_status.handles,'*** Statistics : bootstrap test against reference interval.',1,0);
%loop through files
for filepos=1:length(inputfiles);
    update_status.function(update_status.handles,['Loading : ' inputfiles{filepos}],1,0);
    [header,data]=LW_load(inputfiles{filepos});
    %process
    update_status.function(update_status.handles,'Computing bootstrap. This may take a while!',1,0);
    [header,data]=LW_bootstrap(header,data,channels,baseline_interval,num_bootstrap);
    LW_save(inputfiles{filepos},get(handles.prefixtext,'String'),header,data);
end;
update_status.function(update_status.handles,'Finished!',0,1);
set(handles.figure,'name',fig_title);





