% Begin initialization code - DO NOT EDIT
gui_Singleton = 1;
gui_State = struct('gui_Name',       mfilename, ...
                   'gui_Singleton',  gui_Singleton, ...
                   'gui_OpeningFcn', @GLW_downsample_OpeningFcn, ...
                   'gui_OutputFcn',  @GLW_downsample_OutputFcn, ...
                   'gui_LayoutFcn',  [] , ...
                   'gui_Callback',   []);
if nargin && ischar(varargin{1})
    gui_State.gui_Callback = str2func(varargin{1});
end

if nargout
    [varargout{1:nargout}] = gui_mainfcn(gui_State, varargin{:});
else
    gui_mainfcn(gui_State, varargin{:});
end
% End initialization code - DO NOT EDIT






% --- Executes just before GLW_downsample is made visible.
function GLW_downsample_OpeningFcn(hObject, eventdata, handles, varargin)
% This function has no output args, see OutputFcn.
% hObject    handle to figure
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
% varargin   command line arguments to GLW_downsample (see VARARGIN)
% Choose default command line output for GLW_downsample
handles.output = hObject;
% Update handles structure
guidata(hObject, handles);
%output
set(handles.processbutton,'Userdata',varargin{3});
axis off;
%fill listbox with inputfiles
st=varargin{2};
set(handles.filebox,'String',st);
st=get(handles.filebox,'String');
%load header of first inputfile
header=LW_load_header(st{1});
if header.datasize(4)==1;
    set(handles.ztext,'Visible','off');
    set(handles.zedit,'Visible','off');
end;
if header.datasize(5)==1;
    set(handles.ytext,'Visible','off');
    set(handles.yedit,'Visible','off');
end;
if header.datasize(6)==1;
    set(handles.xtext,'Visible','off');
    set(handles.xedit,'Visible','off');
end;
    




