% --- Executes on button press in processbutton.
function processbutton_Callback(hObject, eventdata, handles)
% hObject    handle to processbutton (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
inputfiles=get(handles.filebox,'String');
update_status=get(handles.processbutton,'UserData');
update_status.function(update_status.handles,'*** Linear channel map.',1,0);
%numlines
numlines=str2num(get(handles.numlines_edit,'String'));
update_status.function(update_status.handles,['Number of lines for channel interpolation : ' num2str(numlines)],1,0);
%loop through files
for filepos=1:length(inputfiles);
    update_status.function(update_status.handles,['Loading : ' inputfiles{filepos}],1,0);    
    [header,data]=LW_load(inputfiles{filepos});
    %process
    update_status.function(update_status.handles,'Computing linear channel map (Thanks to Arnaud Falchier).',1,0);    
    [header,data]=LW_linear_csd_map(header,data,numlines);
    LW_save(inputfiles{filepos},get(handles.prefixtext,'String'),header,data);
end;
update_status.function(update_status.handles,'Finished!',0,1);



